import React from 'react';

import {BrowserRouter as Router , Route, Switch } from 'react-router-dom';
import {ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';
import Home from './pages/Home';
import View from './pages/View';
import AddEdit from './pages/AddEdit';
import About from './pages/About';
import Header from './components/Header';

function App() {
  return (
    <Router>
    <Header/>
    <div className="App">
    <ToastContainer/>
      <Switch>

        <Route exact path ="/" element component={Home}/>
        <Route  path ="/add" element component={AddEdit}/>
        <Route  path ="/update/:id" element component={AddEdit}/>
        <Route  path ="/view/:id" element component={View}/>
        <Route  path ="/about" element component={About}/>

      </Switch>

    </div>
    </Router>

  );
}

export default App;
